def overlay(di, unc='None'):
    sel_res = (2738, 1735)
    image_base = np.zeros((sel_res[1], sel_res[0], 4), dtype=float)
    print(f'IMAGE SHAPE BASE {image_base.shape}')
    step = 128
    az = plt.imread('C:/Users/piero/Test/10002_2/thumbnail/th.png')
    n1, n2, n3, n4, n5 = 0, 0, 0, 0, 0

    print(len(di))
    for i, name_t in enumerate(di):

        shape_x = int(di[name_t]['shape_x'])
        shape_y = int(di[name_t]['shape_y'])
        column = di[name_t]['col']
        row = di[name_t]['row']
        clas = di[name_t]['class']
        print('+++ shapex: {} shapey: {} +++'.format(shape_x, shape_y))
        print(column, row, step)
        c0 = column * step
        r0 = row * step
        print(' R0: {:>5d}  RFIN: {:>5d} \n C0:{:>5d}   CFIN: {:>5d}'.format(r0, r0 + shape_x, c0, c0 + shape_y))
        if unc == 'None':
            if clas == 1:
                image_base[r0:r0 + shape_x, c0:c0 + shape_y, 1] += 0.5
                image_base[r0:r0 + shape_x, c0:c0 + shape_y, 0] += 0.5
                n1 += 1
            elif clas == 2:
                image_base[r0:r0 + shape_x, c0:c0 + shape_y, 1] += 0.3
                image_base[r0:r0 + shape_x, c0:c0 + shape_y, 2] += 0.5
                n2 += 1
            elif clas == 3:
                image_base[r0:r0 + shape_x, c0:c0 + shape_y, 0] += 0.5
                n3 += 1
            elif clas == 4:
                image_base[r0:r0 + shape_x, c0:c0 + shape_y, 1] += 0.5
                n4 += 1
            elif clas == 5:
                image_base[r0:r0 + shape_x, c0:c0 + shape_y, 2] += 0.5
                n5 += 1
            else:
                pass
        elif unc == 'epi':
            image_base[r0:r0 + shape_x, c0:c0 + shape_y, 0] += abs(di[name_t]['epi'])
        elif unc == 'ale':
            image_base[r0:r0 + shape_x, c0:c0 + shape_y, 0] += abs(di[name_t]['ale'])
        elif unc == 'tot':
            image_base[r0:r0 + shape_x, c0:c0 + shape_y, 0] += abs(di[name_t]['epi'])
            image_base[r0:r0 + shape_x, c0:c0 + shape_y, 0] += abs(di[name_t]['ale'])
        else:
            print('cosa passi')
            pass

     # trasparence layer
    image_base[:, :, 3] = 0.5
    image_base = np.where(image_base < 1, image_base, 1)
    print(image_base.shape)
    plt.imshow(az)
    plt.imshow(image_base.astype(np.float))
    #plt.axis('off')
    plt.show()
    if unc == 'None':
        print('N1 --> {:>4}\nN2 --> {:>4}\nN3 --> {:>4}\nN4 --> {:>4}\nN5 --> {:>4}'.format(n1, n2, n3, n4, n5))
